func adder() func(int) int {
	sum := 0
	// return func has access to sum, which is bound to return func
	return func(x int) int {
		sum += x
		return sum
	}
}

func main() {
	// each adder has own context and sum value
	pos, neg := adder(), adder()
	for i := 1; i &lt; 4; i++ {
		fmt.Println(
			pos(i),
			neg(-2*i),
		)
	}
}
// 1 -2
// 3 -6
// 6 -12

