	var test;
	if (bool) {
		test = 4;
	} else {
		test = 2;
	}
	return test; // 2 or 4
}
// Achtung! Das folgende erzeugt nach dem Aufruf eine variable &quot;test&quot; im globalen Kontext! (implicit var)
function varTest(bool) {
	test = 3;
}

// let declarations are block-level (hoisted, but not initialized in temporal dead-zone)
function letTest(bool) {
	var test = 1;
	if (bool) {
		test = 3; // ReferenceError
		let test = 4;
	} else {
		let test = 2;
	}
	return test; // returns 1
}

